mapper.update:
UPDATE COMMUNITY
SET
    TITLE = #{title},
    CONTENT = #{content}
WHERE ID = #{id}

UPDATE COMMUNITY_FILE
    SET
        FILENAME = #{filename},
        FILEORIGINNAME = #{fileoriginname},
        FILEPATH = #{filepath},
        FILETYPE = #{filetype}
    WHERE ID = #{id}
      AND BOARD_ID = #{board_id}

mapper.delete:
DELETE FROM COMMUNITY_FILE
WHERE ID = #{id}
  AND BOARD_ID = #{board_id}
