mapper.select:
SELECT COUNT(*)
     FROM USER
    WHERE USER_ID = #{userId}

SELECT COUNT(*)
     FROM USER
    WHERE NICKNAME = #{nickname}

SELECT *
FROM USER
WHERE USER_ID = #{user_id}
  AND PASSWORD = #{password}

SELECT COUNT(*)
FROM USER
WHERE USER_ID = #{user_id}

SELECT USER_ID
     FROM USER
    WHERE USERNAME = #{username}
      AND EMAIL = #{email}

mapper.update:
UPDATE USER
SET EMAIL = #{email}
WHERE USERNAME = #{username}
  AND BIRTH = #{birth}

mapper.select:
SELECT *
    FROM USER
WHERE USERNAME = #{username}
  AND BIRTH = #{birth}
