mapper.update:
UPDATE QA
SET
    TITLE = #{title},
    CONTENT = #{content},
    DATE = #{date}
WHERE ID = #{id}

UPDATE QA_FILE
SET
    FILENAME = #{filename},
    FILEORIGINNAME = #{fileoriginname},
    FILEPATH = #{filepath},
    FILETYPE = #{filetype}
WHERE ID = #{id}
  AND BOARD_ID = #{board_id}

mapper.delete:
DELETE FROM QA_FILE
WHERE ID = #{id}
  AND BOARD_ID = #{board_id}
